(function(){app.modals.CreateOrEditLanguageModal=function(){var n,i=abp.services.app.language,t=null;this.init=function(i){n=i;n.getModal().find("#LanguageNameEdit").selectpicker({iconBase:"fa",tickIcon:"fa fa-check"});n.getModal().find("#LanguageIconEdit").selectpicker({iconBase:"famfamfam-flag",tickIcon:"fa fa-check"});t=n.getModal().find("form[name=LanguageInformationsForm]")};this.save=function(){var r=t.serializeFormToObject();n.setBusy(!0);i.createOrUpdateLanguage({language:r}).done(function(){abp.notify.info(app.localize("SavedSuccessfully"));n.close();abp.event.trigger("app.createOrEditLanguageModalSaved")}).always(function(){n.setBusy(!1)})}}})(jQuery);