(function(){app.modals.CreateOrganizationUnitModal=function(){var n,i=abp.services.app.organizationUnit,t=null;this.init=function(i){n=i;t=n.getModal().find("form[name=OrganizationUnitForm]");t.validate({ignore:""})};this.save=function(){if(t.valid()){var r=t.serializeFormToObject();n.setBusy(!0);i.createOrganizationUnit(r).done(function(t){abp.notify.info(app.localize("SavedSuccessfully"));n.setResult(t);n.close()}).always(function(){n.setBusy(!1)})}}}})();