(function(){app.modals.LinkAccountModal=function(){var i=abp.services.app.userLink,n,t=null;this.init=function(i){n=i;t=n.getModal().find("form[name=LinkAccountModalForm]");t.validate()};this.save=function(){if(t.valid()){var r=t.serializeFormToObject();n.setBusy(!0);i.linkToUser(r).done(function(t){abp.notify.info(app.localize("SavedSuccessfully"));n.setResult(t);n.close()}).always(function(){n.setBusy(!1)})}}}})();